import React, { Component } from 'react';
import * as firebase from 'firebase';
import QuizConsole from './components/QuizConsole';
import './App.css';


var config = {
  apiKey: "AIzaSyBPLVDzn-VHltDnx-dL1e_M_cPIQQqr8P4",
  authDomain: "adgquiz.firebaseapp.com",
  databaseURL: "https://adgquiz.firebaseio.com",
  projectId: "adgquiz",
  storageBucket: "adgquiz.appspot.com",
  messagingSenderId: "838063692472"
};
firebase.initializeApp(config);

const firebaseUser = firebase.database().ref(`players`);

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      registered: false,
      loggedIn: false,
      teamName: '',
      password: ''
     }
  }

  renderRegisterOrQuiz(){
    if(this.state.loggedIn)
    return(
      <div> <QuizConsole /></div>

    )

    return(
      <div className ="register">
          {this.register()}
      </div>
    )
  }

  teamNameOnChange(event){
    this.setState({ teamName: event.target.value});
    firebaseUser.once('value', (snapshot)=> {
      let players = Object.keys(snapshot.val());
       userCheck = players.includes(this.state.teamName);
       if (userCheck)
       this.setState({registered: true})
       else
       this.setState({registered: false})
    })
  }

  register(){
    return(
      <div className="registerCard container">

      <input type="text" id="TeamName" className="" name="login" placeholder="Team Name" value={this.state.teamName} autoComplete="off" onChange={this.teamNameOnChange.bind(this)}/>
      {/* <input type="password" id="password" className="" name="login" placeholder="password" /> */}
      <br/>
      {/* <input type="submit" className="fadeIn fourth" value="Log In" /> */}

      </div>
    )
  }

  render() { 
    return ( 
      <div className="App">
      {this.renderRegisterOrQuiz()}
      </div>
     );
  }
}


export default App;
