import React, { Component } from "react";
import CircularProgressbar from "react-circular-progressbar";
import * as firebase from "firebase";
import BrandHeader from "./brandHeader";

const needDominantBaselineFix = true;

class QuizConsole extends Component {
  constructor(props) {
    super(props);
    this.state = {
      percentage: 20,
      time: "00:30",
      liveQuestion: 0,
      question: ''
    };
  }

  componentDidMount = () => {
    this.props.firebaseRef.once("value", snapshot => {
      if (snapshot.exists()) {
        let liveQues = snapshot.val().liveQuestion;
        console.log(liveQues);
        this.setState({ liveQuestion: liveQues });
      }
    });
    let firebaseQuestionFind = firebase.database().ref(`question`);
  firebaseQuestionFind.once('value', (snapshot)=> {
    if (snapshot.exists()) {
    console.log(snapshot.val().q3.q);
    this.setState({question: snapshot.val().this.state.liveQuestion.q})
    }

});
  };

