}

  componentDidUpdate() {}

  render() {
    var teamName = this.props.teamName;
    console.log(this.state.liveQuestion)

    return (
      <div>
        <BrandHeader
          teamName={teamName}
          participantName={this.props.participantName}
        />
        <div className="QuestionPage container">
          <div className="text-centre justify-content-center row">
            <p
              className="questionNumber tag"
              style={{ justifyContent: "center" }}
            >
              Quesetion {this.state.liveQuestion} of 10
            </p>
          </div>
          <br />
          <p className="Question">
            {/* Lorem, ipsum dolor sit amet consectetur adipisicing elit. Dolores
            optio sint repellendus quas, quos, omnis alias quidem, adipisicing
            elit. Dolores optio sint repellendus quas, quos, omnis alias quidem */}
            {this.state.question}
          </p>
          <br />
          <div id="options">
            <div className="row text-center justify-content-center">
              <div className="tag col-sm-5" id="a">
                qwertyuiop
              </div>
              <div className="tag col-sm-5" id="b">
                qwertyuiop
              </div>
            </div>
            <br />
            <div className="row text-center justify-content-center">
              <div className="tag col-sm-5" id="c">
                qwertyuiop
              </div>
              <div className="tag col-sm-5" id="d">
                qwertyuiop
              </div>
            </div>
          </div>
          <br />
          <div className="row">
            <div className="col-sm-5" />
            <div className="timer col-sm-4">
              <CircularProgressbar
                className="timerCircle"
                percentage={this.state.percentage}
                text={
                  <tspan
                    dx={needDominantBaselineFix ? -25 : 0}
                    dy={needDominantBaselineFix ? +6 : 0}
                  >
                    {this.state.time}
                  </tspan>
                }
                background="true"
                backgroundPadding={0}
                strokeWidth={5}
                styles={{
                  // Customize the root svg element
                  root: {},
                  // Customize the path, i.e. the "completed progress"
                  path: {
                    // Path color
                    stroke: `rgba(250, 0, 250, ${this.state.percentage / 100})`,
                    // Whether to use rounded or flat corners on the ends - can use 'butt' or 'round'
                    strokeLinecap: "butt",
                    // Customize transition animation
                    transition: "stroke-dashoffset 0.5s ease 0s"
                  },
                  // Customize the circle behind the path, i.e. the "total progress"
                  trail: {
                    // Trail color
                    stroke: "#d6d6d6"
                  },
                  // Customize the text
                  text: {
                    // Text color
                    fill: "#34c",
                    // Text size
                    fontSize: "20px"
                  },
                  // Customize background - only used when the `background` prop is true
                  background: {
                    fill: "white"
                  }
                }}
              />
            </div>

            <div
              className="continue tag col-sm-2 text-center"
              style={{ borderRadius: 25 }}
            >
              Continue
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default QuizConsole;
