import React, { Component } from "react";
import CircularProgressbar from "react-circular-progressbar";
import * as firebase from "firebase";
import BrandHeader from "./brandHeader";

const needDominantBaselineFix = true;

class QuizConsole extends Component {
  constructor(props) {
    super(props);

    this.state = {
      percentage: 20,
      time: "00:30",
      liveQuestion: 0,
      question: "",
      correctAnswer: "",
      optiona: "",
      optionb: "",
      optionc: "",
      optiond: "",
      answer: "",
      answered: false,
      counter: 1,
      score: 0,
    };
  }

  componentDidMount = () => {
    this.props.firebaseRef.on("value", snapshot => {
      if (snapshot.exists()) {
        console.log(snapshot.val().liveQuestion);
        this.setState({ liveQuestion: snapshot.val().liveQuestion,
        counter: 1 });
        this.updateLiveQuestion();
      }
    });

    let firebaseQuestionFind = this.props.database.ref(
      `question/q${this.state.liveQuestion}`
    );
    firebaseQuestionFind.on("value", snapshot => {
      console.log(snapshot.val().q);
      this.setState({ question: snapshot.val().q });
    });
    let firebaseUserInfo = this.props.database.ref(`players/${this.props.teamName}`);
        firebaseUserInfo.once('value', (snapshot) =>{

            this.setState({counter: snapshot.val().counter,
            score: snapshot.val().score})
        })
  };

  updateLiveQuestion = () => {
    let firebaseQuestionFind = this.props.database.ref(
      `question/q${this.state.liveQuestion}`
    );
    firebaseQuestionFind.on("value", snapshot => {
      console.log(snapshot.val().q);
      console.log(snapshot.val().a);
      console.log(snapshot.val().optionA);
      this.setState({
        question: snapshot.val().q,
        correctAnswer: snapshot.val().a,
        optiona: snapshot.val().optionA,
        optionb: snapshot.val().optionB,
        optionc: snapshot.val().optionC,
        optiond: snapshot.val().optionD,
        answered: false,
      });
    });

  };

  removeDisabled=()=>{
    document
    .querySelector("#a")
    .removeAttribute("disabled");
    document
    .querySelector("#b")
    .removeAttribute("disabled");
    document
    .querySelector("#c")
    .removeAttribute("disabled");
    document
    .querySelector("#d")
    .removeAttribute("disabled");
  }

  updateAnswer = event => {
    this.removeDisabled()
    document
      .querySelector(`#${event.target.id}`)
      .setAttribute("disabled", true);
    this.setState({ answer: event.target.id,
                    answered: true ,
                  counter: 2});
    let firebaseChangeCounter2 = this.props.database.ref(`players/${this.props.teamName}`);
    firebaseChangeCounter2.child('counter').set(2);
    let firebaseUserInfo = this.props.database.ref(`players/${this.props.teamName}`);
        firebaseUserInfo.once('value', (snapshot) =>{

            this.setState({counter: snapshot.val().counter,
            score: snapshot.val().score})
        })

        this.optionUpdate();


       if (this.state.answer === this.state.correctAnswer ) {
        this.props.firebaseRef.once('value', (snapshot)=> {
          let startTimeStamp = snapshot.val().timeStamp;
        let currentTime = new Date().getTime();
        let timeElapsed =  Math.round((currentTime-startTimeStamp)/1000);
        let newScore = this.state.score + (50-timeElapsed);
        let firebaseChangeScore = this.prop.database.ref(`players/${this.state.teamName}`);
        firebaseChangeScore.child("score").set(newScore);
        this.setState({score: newScore});
        })

    }

  };

  optionUpdate=()=>{
    let firebaseQuestionFind2 = this.props.database.ref(`question/q${this.state.liveQuestion}`);

    let optionACounter;
    let optionBCounter;
    let optionCCounter;
    let optionDCounter;
    firebaseQuestionFind2.once('value', function(snapshot) {
        optionACounter = snapshot.val().optionACounter;
        optionBCounter = snapshot.val().optionBCounter;
        optionCCounter = snapshot.val().optionCCounter;
        optionDCounter = snapshot.val().optionDCounter;
    })

    if (this.state.answer === 'a') {
        optionACounter = optionACounter + 1;
        firebaseQuestionFind2.child('optionACounter').set(optionACounter);
    }

    if (this.state.answer === 'b') {
        optionBCounter = optionBCounter + 1;
        firebaseQuestionFind2.child('optionBCounter').set(optionBCounter);
    }

    if (this.state.answer === 'c') {
        optionCCounter = optionCCounter + 1;
        firebaseQuestionFind2.child('optionCCounter').set(optionCCounter);
    }

    if (this.state.answer === 'd') {
        optionDCounter = optionDCounter + 1;
        firebaseQuestionFind2.child('optionDCounter').set(optionDCounter);
    }
  }

  renderOptions = () => {
    if(this.state.answered===false && this.state.counter===1)
    return (
      <div>
        <div className="row text-center justify-content-center">
          <button
            className="tag col-sm-5"
            id="a"
            onClick={this.updateAnswer.bind(this)}
          >
            {this.state.optiona}
          </button>
          <button
            className="tag col-sm-5"
            id="b" 
            onClick={this.updateAnswer.bind(this)}
          >
            {this.state.optionb}
          </button>
        </div>
        <br />
        <div className="row text-center justify-content-center">
          <button
            className="tag col-sm-5"
            id="c"
            onClick={this.updateAnswer.bind(this)}
          >
            {this.state.optionc}
          </button>
          <button
            className="tag col-sm-5"
            id="d"
            onClick={this.updateAnswer.bind(this)}
          >
            {this.state.optiond}
          </button>
        </div>
      </div>
    );

    return(
      <div id="answeredOption">
         <div className="row text-center justify-content-center">
          <button
            className="tag col-sm-5"
            id="a"
          >
            {this.state.optiona}
          </button>
          <button
            className="tag col-sm-5"
            id="b"
          >
            {this.state.optionb}
          </button>
        </div>
        <br />
        <div className="row text-center justify-content-center">
          <button
            className="tag col-sm-5"
            id="c"
          >
            {this.state.optionc}
          </button>
          <button
            className="tag col-sm-5"
            id="d"
          >
            {this.state.optiond}
          </button>
        </div>
        <div className="row" >
          <p id="waitNext">Wait for next question</p>
        </div>
      </div>
    )
  };

  render() {
    // console.log(quesLive)

    return (
      <div>
        <BrandHeader
          teamName={this.props.teamName}
          participantName={this.props.participantName}
        />
        <div className="QuestionPage container">
          <div className="text-centre justify-content-center row">
            <p
              className="questionNumber tag"
              style={{ justifyContent: "center" }}
            >
              Quesetion {this.state.liveQuestion} of 10
            </p>
          </div>
          <br />
          <p className="Question">
            {/* Lorem, ipsum dolor sit amet consectetur adipisicing elit. Dolores
            optio sint repellendus quas, quos, omnis alias quidem, adipisicing
            elit. Dolores optio sint repellendus quas, quos, omnis alias quidem */}
            {this.state.question}
          </p>
          <br />
          <div id="options">{this.renderOptions()}</div>
          <br />
          <div className="row">
            <div className="col-sm-5" />
            <div className="timer col-sm-4">
              <CircularProgressbar
                className="timerCircle"
                percentage={this.state.percentage}
                text={
                  <tspan
                    dx={needDominantBaselineFix ? -25 : 0}
                    dy={needDominantBaselineFix ? +6 : 0}
                  >
                    {this.state.time}
                  </tspan>
                }
                background="true"
                backgroundPadding={0}
                strokeWidth={5}
                styles={{
                  // Customize the root svg element
                  root: {},
                  // Customize the path, i.e. the "completed progress"
                  path: {
                    // Path color
                    stroke: `rgba(250, 0, 250, ${this.state.percentage / 100})`,
                    // Whether to use rounded or flat corners on the ends - can use 'butt' or 'round'
                    strokeLinecap: "butt",
                    // Customize transition animation
                    transition: "stroke-dashoffset 0.5s ease 0s"
                  },
                  // Customize the circle behind the path, i.e. the "total progress"
                  trail: {
                    // Trail color
                    stroke: "#d6d6d6"
                  },
                  // Customize the text
                  text: {
                    // Text color
                    fill: "#34c",
                    // Text size
                    fontSize: "20px"
                  },
                  // Customize background - only used when the `background` prop is true
                  background: {
                    fill: "white"
                  }
                }}
              />
            </div>

            <div
              className="continue tag col-sm-2 text-center"
              style={{ borderRadius: 25 }}
            >
              Continue
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default QuizConsole;
