import React, { Component } from 'react';
import * as firebase from 'firebase';
import QuizConsole from './components/QuizConsole';
import './App.css';


var config = {
  apiKey: "AIzaSyBPLVDzn-VHltDnx-dL1e_M_cPIQQqr8P4",
  authDomain: "adgquiz.firebaseapp.com",
  databaseURL: "https://adgquiz.firebaseio.com",
  projectId: "adgquiz",
  storageBucket: "adgquiz.appspot.com",
  messagingSenderId: "838063692472"
};
firebase.initializeApp(config);

const firebaseUser = firebase.database().ref(`players`);

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      registered: false,
      loggedIn: false,
      teamName: '',
      password: ''
     }
  }

  renderRegisterOrQuiz(){
    if(this.state.loggedIn)
    return(
      <div> <QuizConsole /></div>

    )

    return(
      <div className ="register">
          {this.register()}
      </div>
    )
  }

  teamNameOnChange(event){
    this.setState({ teamName: event.target.value});
    firebaseUser.once('value', (snapshot)=> {
      let players = Object.keys(snapshot.val());
        let userCheck = players.includes(this.state.teamName);
       if (userCheck)
       this.setState({registered: true})
       else
       this.setState({registered: false})
    })

  }

  passwordOnChange(event){
    this.setState({ password: event.target.value});
  }

  onPressRegisterButton(){
    if(this.state.registered===false)
    {
      return(
        <div>
      <input type="text" id="ParticipantName" className="" name="login" placeholder="Participant Name" value={this.state.participantName} autoComplete="off" onChange={this.participantNameOnChange.bind(this)}/>
      <input type="password" id="password" className="" name="login" placeholder="password" value={this.state.password} onChange={this.passwordOnChange.bind(this)}/>
        </div>
      )
    }
  }

  loginOrRegister(){
    if(this.state.registered)
    return(
      <input type="password" id="password" className="" name="login" placeholder="password" value={this.state.password} onChange={this.passwordOnChange.bind(this)}/>
    )

    return(
      <div>
        <div className="btn btn-primary" id="rButton" onClick={this.onPressRegisterButton.bind(this)}> Register </div>
      {/* <input type="text" id="TeamName" className="" name="login" placeholder="Team Name" value={this.state.teamName} autoComplete="off" onChange={this.teamNameOnChange.bind(this)}/>
      <input type="password" id="password" className="" name="login" placeholder="password" value={this.state.password} onChange={this.passwordOnChange.bind(this)}/> */}
      </div>
    )
  }

  register(){
    return(
      <div className="registerCard container">

      <input type="text" id="TeamName" className="" name="login" placeholder="Team Name" value={this.state.teamName} autoComplete="off" onChange={this.teamNameOnChange.bind(this)}/>
      {this.loginOrRegister()}

      {/* <input type="submit" className="fadeIn fourth" value="Log In" /> */}

      </div>
    )
  }

  render() { 
    return ( 
      <div className="App">
      {this.renderRegisterOrQuiz()}
      </div>
     );
  }
}


export default App;
